mapper.select:
SELECT m.id, m.title, m.description, m.category_id AS categoryId, m.img, m.create_time AS createTime, m.update_time AS updateTime, m.src, m.singer, m.size, m.time, m.hits, m.comments, m.loves, m.status, m.is_delete, m.user_id AS userId, u.username, u.header_pic AS headerPic FROM music m LEFT JOIN category c ON c.id = m.category_id LEFT JOIN USER u ON u.userid = m.user_id
and m.id != #{filterId}
and m.status = #{status}
and m.is_delete = #{isDelete}
and m.category_id = #{typeId}
ORDER BY m.${order} LIMIT #{pageNo},#{pageSize}

mapper.update:
update music
hits=#{hits}, collections=#{collections}, loves=#{loves}, comments=#{comments}, status=#{status}, is_delete=#{isDelete},
where id = #{id}
